import React, { useState } from 'react'
import firebase from '../congif/firebase';
import "firebase/auth";
import { Redirect, useHistory } from 'react-router-dom';

export default function Login() {
    const [isLoading, setisLoading] = useState(false);
    const [error, seterror] = useState('');
    const [form, setform] = useState({ email: "", password: "" });
    // const [isLoggedIn, setisLoggedIn] = useState(false);
    const history = useHistory();

    function handleForm(e) {
        if (isLoading) return;
        setisLoading(true);
        e.preventDefault();
        firebase.auth().signInWithEmailAndPassword(form.email, form.password).then(res => {
            setisLoading(false);
            seterror('');
            history.replace('/');
            // setisLoggedIn(true)
        }).catch((error) => {
            setisLoading(false)
            seterror(error.message);
        });
    }
    function handleInput(e) {
        setform({ ...form, [e.target.name]: e.target.value })
        console.log(e.target.value, e.target.name);
    }
    // if (isLoggedIn) return <Redirect to='/' />;

    return (
        <div className='flex h-screen bg-gray-200'>
            <div className='m-auto w-1/3 text-white flex flex-wrap justify-center shadow-lg rounded-lg bg-gradient-to-br from-indigo-900 to-indigo-700'>
                <form className='m-5 w-10/12' onSubmit={handleForm}>
                    {error !== '' && <p>{error}</p>}
                    <h1 className='w-full text-4xl tracking-widest text-center my-6'>Login</h1>
                    <div className='w-full my-6'>
                        <input type='email' className='p-2 rounded shadow w-full text-black' placeholder='Email or Username' name='email' value={form.email} onChange={handleInput} />
                    </div>
                    <div className='w-full my-6'>
                        <input type='password' className='p-2 rounded shadow w-full text-black' placeholder='password' name='password' value={form.password} onChange={handleInput} />
                    </div>
                    <div className='w-full my-10'>
                        <button type='submit' className='p-2 rounded shadow w-full bg-gradient-to-tr from-yellow-600 to-yellow-400 text-black'>
                            {isLoading ? (<i className='fas fa-circle-notch fa-spin '></i>) : ('Login')}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    )
}
