import React, { useState } from 'react';

export default function Images() {
    // const [myInterval, setmyInterval] = useState(null);
    // useEffect(() => {
    //     console.log('Images comp mounted');
    //     const interval = setInterval(() => {
    //         console.log('Hello');
    //     }, 1000);
    //     return () => {
    //         clearInterval(interval);
    //         console.log('Images comp unmounted');
    //     }
    // }, []);
    const [images, setimages] = useState([
        "https://images.unsplash.com/photo-1531608139434-1912ae0713cd?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1051&q=80",
        "https://images.unsplash.com/photo-1606787366850-de6330128bfc?ixid=MnwxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80",
        "https://images.unsplash.com/photo-1624572920604-b3886c4c0639?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1051&q=80",
        "https://images.unsplash.com/photo-1624614014891-0ac647fc1f65?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80",
        "https://images.unsplash.com/photo-1606787366850-de6330128bfc?ixid=MnwxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80",
    ]);
    const [newImageUrl, setnewImageUrl] = useState('');
    const [isHovering, setisHovering] = useState(false)

    //component inside component
    function ShowImages() {
        return (images.map((image, index) => {
            return (
                <div className='w-1/3 my-4 flex justify-center' key={index}>
                    <div className='relative'>
                        <i className={`fas fa-times absolute right-0 cursor-pointer opacity-25 hover:opacity-100 ${isHovering ? '' : 'hidden'}`} onClick={() => handleRemove(index)}></i>
                        <img onMouseEnter={() => setisHovering(true)} onMouseOut={() => setisHovering(false)} src={image} alt='nature' width='150' />
                    </div>
                </div>
            )
        }))
    }
    function handleAdd() {
        // let tempImage = [...images];
        // tempImage.push('https://images.unsplash.com/photo-1472898965229-f9b06b9c9bbe?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80');
        // console.log(tempImage);
        // setimages(tempImage)
        // setimages([...images, 'https://images.unsplash.com/photo-1472898965229-f9b06b9c9bbe?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80']);
        // setimages(['https://images.unsplash.com/photo-1472898965229-f9b06b9c9bbe?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80', ...images]);
        if (newImageUrl !== '') {
            setimages([...images, newImageUrl]);
            setnewImageUrl('');
        }
    }
    function handleChange(event) {
        setnewImageUrl(event.target.value);
    }
    function handleRemove(index) {
        // setimages(images.filter((image, i) => i !== index));
        setimages([...images.slice(0, index), ...images.slice(index + 1, images.length)])
    }
    // function handleMouseEnter() {
    //     setisHovering(true)
    // }
    // function handleMouseOut() {
    //     setisHovering(false)
    // }
    return (
        <section>
            <div className='flex flex-wrap justify-center'>
                <ShowImages />
            </div>
            <div className='flex justify-between my-2'>
                <div className='w-full'>
                    <input type='text' className='w-full p-2 border border-gray-800 shadow rounded' onChange={handleChange} value={newImageUrl} />
                </div>
                <div>
                    <button disabled={newImageUrl === ''} className={`p-2 ml-2 text-white rounded ${newImageUrl !== '' ? 'bg-green-600' : 'bg-green-300'}`} onClick={handleAdd}>Add</button>
                </div>
            </div>

        </section>
    );
}

