import React, { useState } from 'react';
import { motion } from 'framer-motion';

export default function Image({ image, index, handleRemove }) {
    const [isHovering, setIsHovering] = useState(false);
    const [showPreview, setShowPreview] = useState(false)

    return (
        <div className='w-1/6 p-1 border flex justify-center'>
            <div className='relative' onMouseEnter={() => setIsHovering(true)} onMouseLeave={() => setIsHovering(false)}>
                <i className={`fas fa-times absolute right-0 cursor-pointer opacity-25 hover:opacity-100 ${isHovering ? '' : 'hidden'}`} onClick={() => handleRemove(index)}></i>
                <img onClick={() => setShowPreview(true)} src={image} alt='nature' width='100%' height='auto' />
            </div>
            {showPreview && <section className='fixed w-full h-full flex justify-center items-center top-0 left-0 z-40' onClick={() => setShowPreview(false)}>
                <p>Preview</p>
            </section>}
        </div>
    );
}
