import axios from 'axios';
import React, { useEffect, useState } from 'react';

const api = process.env.REACT_APP_UNSPLASH_API;
const secret = process.env.REACT_APP_UNSPLASH_KEY;
export default function useFetchImage(page, searchTerm) {
    const [Images, setImages] = useState([]);
    const [errors, setErrors] = useState([]);
    const [isLoading, setisLoading] = useState(false);
    function fetchSearch() {
        axios.get(`${api}/search/photos?client_id=${secret}&page=${page}&query=${searchTerm}`).then(res => {
            if (page > 1) {

                setImages([...res.data.results]);
            } else {
                setImages([...res.data.results]);
            }
            setisLoading(false);
        }).catch((e) => {
            setErrors(["Unable to fetch images"]);
            setisLoading(false);
        });
    }
    function fetchRandom() {
        axios.get(`${api}/photos?client_id=${secret}&page=${page}`).then(res => {
            setImages([...Images, ...res.data]);
            setisLoading(false);
        }).catch((e) => {
            setErrors(["Unable to fetch images"]);
            setisLoading(false);
        });
    }

    useEffect(() => {
        setisLoading(true);

        // const url = searchTerm === null ? 'photos' : 'search/photos';
        // axios.get(`${api}/${url}?client_id=${secret}&page=${page}&query=${searchTerm}`)
        if (searchTerm !== null) {
            fetchSearch();
        } else {
            fetchRandom();
        }
    }, [page]);
    useEffect(() => {
        if (searchTerm === null) return;
        fetchSearch();
    }, [searchTerm])

    return [Images, setImages, errors, isLoading];
}
