import React, { useEffect, useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import firebase from '../congif/firebase'

export default function Header() {
    const [isLoggedIn, setisLoggedIn] = useState(false);
    const history = useHistory();

    useEffect(() => {
        firebase.auth().onAuthStateChanged((user) => {
            if (user) {
                setisLoggedIn(true);
            }
        })
    }, []);

    function logout() {
        firebase.auth().signOut().then(res => {
            setisLoggedIn(false);
            history.replace('/login');
        }).catch(e => {
            console.log(e.response.data);
        })
    }

    return (
        <nav className='py-5 bg-gray-900 text-white'>
            <ul className='flex justify-between px-10'>
                <span className='flex'>
                    <li className='mr-5'>
                        <Link to="/" className={`hover:underline hover:text-blue-200`}>Home</Link>
                    </li>
                    <li className='mr-5'>
                        <Link to="/gallery" className={`hover:underline hover:text-blue-200`}>Gallery</Link>
                    </li>
                </span>
                <li>
                    {isLoggedIn ? (<button onClick={logout}>Logout</button>) : (<Link to="/login" className={`hover:underline hover:text-blue-200`}>Login</Link>)}
                </li>
            </ul>
        </nav >
    )
}
