import { useFormik } from 'formik';
import React from 'react';

export default function SignUp() {

    const formik = useFormik({
        initialValues: {
            email: '',
            password: ''
        },
        validate: value => {
            const errors = {};
            if (!value.email) {
                errors.email = "Email field is Required.";
            } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
                errors.email = 'Invalid email address';
            }

            return errors;
        },
        onSubmit: value => {
            console.log(value);
        }
    });

    return (
        <div className='flex h-screen bg-gray-200'>
            <div className='bg-white flex flex-wrap justify-center m-auto rounded-lg shadow-lg w-1/3'>
                <form className='m-5 w-10/12' onSubmit={formik.handleSubmit}>
                    <h1 className='w-full text-4xl tracking-widest text-center my-6'>Sign Up Here</h1>
                    <div className='w-full my-6'>
                        <input type='email' className='border-b-2 border-dashed border-gray-300 py-1.5 text-black w-full' placeholder='Email or Username' name='email' onChange={formik.handleChange} value={formik.values.email} />
                        {formik.touched.email && formik.errors.email ? (
                            <div>{formik.errors.email}</div>
                        ) : null}
                    </div>
                    <div className='w-full my-6'>
                        <input type='password' className='border-b-2 border-dashed border-gray-300 py-1.5 text-black w-full' placeholder='password' name='password' onChange={formik.handleChange} value={formik.values.password} />
                    </div>
                    <div className='w-full my-10'>
                        <button type='submit' className='bg-red-300 p-2 rounded shadow text-white w-full'>Sign Up
                            {/* {isLoading ? (<i className='fas fa-circle-notch fa-spin '></i>) : ('Sign Up')} */}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    )
}
