import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import firebase from '../congif/firebase'

export default function Header() {
    const [isLoggedIn, setisLoggedIn] = useState(false);

    useEffect(() => {
        firebase.auth().onAuthStateChanged((user) => {
            setisLoggedIn(true)
        })
    }, [])

    return (
        <nav className='py-5 bg-gray-900 text-white'>
            <ul className='flex justify-between px-10'>
                <span className='flex'>
                    <li className='mr-5'>
                        <Link to="/" className={`hover:underline hover:text-blue-200`}>Home</Link>
                    </li>
                    <li className='mr-5'>
                        <Link to="/gallery" className={`hover:underline hover:text-blue-200`}>Gallery</Link>
                    </li>
                </span>
                <li>
                    <Link to="/login" className={`hover:underline hover:text-blue-200`}>Login</Link>
                </li>
            </ul>
        </nav >
    )
}
