        "https://images.unsplash.com/photo-1606787366850-de6330128bfc?ixid=MnwxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80",
    ]);

    //component inside component
    function ShowImages() {
        return (Images.map(image => {
            return (
                <div>
                    <img src={image} alt='nature' width='150' />
                </div>
            )
        }))
    }
    function handleAdd() {
        Images.push('https://images.unsplash.com/photo-1472898965229-f9b06b9c9bbe?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80');
        console.log(Images);
    }
    return (
        <section>
            <div className='flex justify-center'>
                <ShowImages />
            </div>
            <div className='flex justify-between my-2'>
                <input type='text' className='p-2 border border-gray-800 shadow rounded' />
                <button className='p-2 bg-green-600 text-white rounded' onClick={handleAdd}>Add New</button>
            </div>

        </section>
    );
}

