import React, { Component } from 'react';
import './assets/css/style.css';

// function App({ title }) {
//     return (
//         <div>
//             <div className='bg-gray-400 text-white p-5 border border-solid border-blue-600'>{title}</div>
//         </div>
//     );
// }

class App extends Component {
    constructor(props) {
        console.log('App Constructor');
        super(props);
        this.state = { 'title': 'Hello React', isShowing: false };
        this.handleClick = this.handleClick.bind(this);
    }
    //states are immutable
    // handleClick = () => { this.setState({ isShowing: !this.state.isShowing }) }
    handleClick() { this.setState({ isShowing: !this.state.isShowing }) }
    render() {
        console.log('App Render');
        return (
            <section className='flex justify-center text-center'>
                <div className='w-1/2'>
                    <div className='my-4'>{this.state.title}</div>
                    <div>
                        <button onClick={this.handleClick} className='my-2 p-1 bg-blue-700 text-white'>Toggle image</button>
                    </div>
                    {this.state.isShowing ? (<img src='https://images.unsplash.com/photo-1531608139434-1912ae0713cd?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1051&q=80' alt='bi-cycle' />) : null}
                </div>
            </section>
        );
    }
}


export default App;
