    const [images, setimages] = useState([
        "https://images.unsplash.com/photo-1531608139434-1912ae0713cd?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1051&q=80",
        "https://images.unsplash.com/photo-1624572920604-b3886c4c0639?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1051&q=80",
        "https://images.unsplash.com/photo-1624614014891-0ac647fc1f65?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80",
        "https://images.unsplash.com/photo-1606787366850-de6330128bfc?ixid=MnwxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80",
    ]);
    const [newImageUrl, setnewImageUrl] = useState(null);

    //component inside component
    function ShowImages() {
        return (images.map(image => {
            return (
                <div className='w-1/3'>
                    <img className='my-0 mx-auto' src={image} alt='nature' width='150' />
                </div>
            )
        }))
    }
    function handleAdd() {
        // let tempImage = [...images];
        // tempImage.push('https://images.unsplash.com/photo-1472898965229-f9b06b9c9bbe?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80');
        // console.log(tempImage);
        // setimages(tempImage)
        // setimages([...images, 'https://images.unsplash.com/photo-1472898965229-f9b06b9c9bbe?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80']);
        // setimages(['https://images.unsplash.com/photo-1472898965229-f9b06b9c9bbe?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80', ...images]);
        setimages([...images, newImageUrl]);
        setnewImageUrl('');
    }
    function handleChange(event) {
        if (event.target.value !== '') {
            setnewImageUrl(event.target.value);
        }
        console.log(newImageUrl)

    }
    return (
        <section>
            <div className='flex flex-wrap justify-center'>
                <ShowImages />
            </div>
            <div className='flex justify-around my-2'>
                <input type='text' className='p-2 border border-gray-800 shadow rounded' onChange={handleChange} value={newImageUrl} />
                <button className='p-2 bg-green-600 text-white rounded' onClick={handleAdd}>Add New</button>
            </div>

        </section>
    );
}

